import React from 'react';
import '../App.css';


const Form = ({setTodos}) =>{

  const todoHandler =(e)=>{
    console.log(e.target.value);
    setTodos(e.target.value);
  };
  const submitTodoHandler =(e)=>{
    e.preventDefault();
  }
  return(

      <form>
        <input onChange={todoHandler} className="todo-input" type="text"/>

        <select name="todos" className="todo-type">
            <option value="Daily">Daily</option>
            <option value="Work">Work</option>
            <option value="School">School</option>
            <option value="Others">Others</option>
        </select>

        <button onClikc={submitTodoHandler} type="submit">Add</button>
      </form>

  );
}

export default Form
