import React, {useState} from 'react';
import './App.css';

import Form from './components/Form';
import TodoList from './components/TodoList';


function App() {
  const [todos,setTodos] = useState("");
  return (
    <div className="App">
      <header>
        <h1>Orkun's Todo List</h1>
      </header>
      <Form setTodos={setTodos}/>
      <TodoList todos={todos}/>
    </div>
  );
}

export default App;
